import Post from "../Post";
import {useEffect, useState} from "react";
import PostListByAuthor from "../dropdown";

export default function IndexPage(author) {
  const [users, setUsers] = useState([]);
  const [selectedAuthor, setSelectedAuthor] = useState('');
  
  const [posts,setPosts] = useState([]);
  useEffect(() => {
    fetch('http://localhost:4000/post').then(response => {
      response.json().then(posts => {
        setPosts(posts);
      });
    });
  }, []);
  return ( 
    <>
      <div>
  <select onChange={e => setSelectedAuthor(e.target.value)} value={selectedAuthor}>
    <option value="">Select an author</option>
    {users.map(user => (
      <option key={user._id} value={user._id}>{author.username}</option>
    ))}
  </select>
    </div>
      {posts.length > 0 && posts.map(post => (
        <Post {...post} />
      ))}
    </>
  );
}
